datasource.query:

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
    SELECT ?c ?l ?a
    WHERE {
    ?c rdf:type skos:Concept.
    <http://vocab.nerc.ac.uk/collection/P10/current/> skos:member ?c.
    OPTIONAL {
    ?c skos:prefLabel ?l.
    FILTER( LANG(?l) = "en")
    }
    OPTIONAL {
    ?c skos:prefLabel ?a.
    FILTER( LANG(?a) = "it")
    }
    FILTER( REGEX( STR(?l), "$search_param", "i") || REGEX( STR(?a), "$search_param", "i") )
    }
    ORDER BY ASC(?l) ASC(?a)


PREFIX skos: <http://www.w3.org/2004/02/skos/core#> 
SELECT ?c ?l ?a
FROM <http://ritmare.it/rdfdata/parameters>
WHERE { 
  ?c rdf:type skos:Concept.
  OPTIONAL {
    ?c skos:prefLabel ?l.
    FILTER( LANG(?l) = "en")
  }
  OPTIONAL {
    ?c skos:prefLabel ?a.
    FILTER( LANG(?a) = "it")
  }
  FILTER( REGEX( STR(?l), "$search_param", "i") || REGEX( STR(?a), "$search_param", "i") )
}
ORDER BY ASC(?l) ASC(?a)


PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
SELECT ?c ?l ?a
FROM <http://ritmare.it/rdfdata/parameters>
WHERE {
  ?c rdf:type skos:Concept.
  OPTIONAL {
    ?c skos:prefLabel ?l.
    FILTER( LANG(?l) = "en")
  }
  OPTIONAL {
    ?c skos:prefLabel ?a.
    FILTER( LANG(?a) = "it")
  }
  FILTER( REGEX( STR(?c), "$search_param", "i") || REGEX( STR(?a), "$search_param", "i") )
}
ORDER BY ASC(?l) ASC(?a)


PREFIX addr: <http://wymiwyg.org/ontologies/foaf/postaddress#>
PREFIX vcard: <http://www.w3.org/2006/vcard/ns#>
SELECT ?phone fn:concat(?l1, ', ', ?l2) AS ?addr ?town ?region ?postcode ?country ?email ?homepage
FROM <http://ritmare.it/rdfdata/manufacturers>
WHERE { 
  OPTIONAL {
    <$search_param> foaf:phone ?phone .
  }
  OPTIONAL {
    <$search_param> vcard:email ?email.
  }
  OPTIONAL {
    <$search_param> foaf:homepage ?homepage.
  }
  OPTIONAL {
    <$search_param> addr:address ?b1.
  }
  OPTIONAL {
    ?b1 addr:deliveryPoint ?b2.
  }
  OPTIONAL {
    ?b2 addr:location ?b3.
  }
  OPTIONAL {
    ?b3 addr:thoroughfareName ?l1.
  }
  OPTIONAL {
    ?b3 addr:streetNr ?l2.
  }
  OPTIONAL {
    ?b3 addr:town ?town.
  }
  OPTIONAL {
    ?b3 addr:region ?region.
  }
  OPTIONAL {
    ?b3 addr:postcode ?postcode.
  }
  OPTIONAL {
    ?b3 addr:country ?country.
  }
}


PREFIX skos: <http://www.w3.org/2004/02/skos/core#>        
SELECT ?c ?l ?a ?urn                       
FROM <http://ritmare.it/rdfdata/parameters>
WHERE { 
  ?c rdf:type skos:Concept.
  OPTIONAL {
    ?c skos:notation ?urn. 
  }
  OPTIONAL {
    ?c skos:prefLabel ?l.
    FILTER( LANG(?l) = 'en') 
  }
  OPTIONAL {
    ?c skos:prefLabel ?a.
    FILTER( LANG(?a) = 'it') 
  }
  FILTER( REGEX( STR(?l), '$search_param', 'i') || REGEX( STR(?a), '$search_param', 'i') ) 
}
ORDER BY ASC(?l) ASC(?a)


PREFIX skos: <http://www.w3.org/2004/02/skos/core#> 
SELECT ?c ?l ?a ?urn
WHERE { 
  ?c rdf:type skos:Concept.
  ?c skos:inScheme <http://vocab.nerc.ac.uk/collection/L05/current/>.
  OPTIONAL {
    ?c skos:notation ?urn.
  }
  OPTIONAL {
    ?c skos:prefLabel ?l.
    FILTER( LANG(?l) = "en")
  }
  OPTIONAL {
    ?c skos:prefLabel ?a.
    FILTER( LANG(?a) = "it")
  }
  FILTER( REGEX( STR(?l), "$search_param", "i") || REGEX( STR(?a), "$search_param", "i") )
}
ORDER BY ASC(?l) ASC(?a)


PREFIX skos: <http://www.w3.org/2004/02/skos/core#> 
SELECT ?c ?l ?urn
FROM <http://ritmare.it/rdfdata/units>
WHERE { 
  ?c rdf:type skos:Concept.
  OPTIONAL {
    ?c skos:prefLabel ?l.
    FILTER( LANG(?l) = "en")
  }
  OPTIONAL {
    ?c skos:prefLabel ?a.
    FILTER( LANG(?a) = "en")
  }
  OPTIONAL {
    ?c skos:altLabel ?urn.
    FILTER( LANG(?urn) = "en")
  }
  FILTER( REGEX( STR(?l), "$search_param", "i") || REGEX( STR(?a), "$search_param", "i") )
}
ORDER BY ASC(?l) ASC(?a)


                PREFIX vcard: <http://www.w3.org/2006/vcard/ns#>
SELECT '' AS ?phone '' AS ?addr ?email '' AS ?town '' AS ?country '' AS ?postcode ''AS ?homepage ?fullname ?l
WHERE {
  OPTIONAL {
    <$search_param> foaf:fullName ?fullname .
  }
  OPTIONAL {
    <$search_param> vcard:email ?email .
  }
                          OPTIONAL {
                            <$search_param> vcard:org ?b3 .
                            ?b3 foaf:homepage ?homepage .
                            ?b3 foaf:name ?l .
                          }
FILTER(lang(?l)='en')
}
LIMIT 1


