sparql.query:

PREFIX skos: <http://www.w3.org/2004/02/skos/core#> SELECT * WHERE {
    SERVICE <http://vocabs.ceh.ac.uk/evn/tbl/sparql> {
        GRAPH <urn:x-evn-pub:envthes>{
            {
                BIND (<http://vocabs.lter-europe.net/EnvThes/20935> as ?root)
                ?root (skos:narrower*) ?c.
                ?c skos:prefLabel ?l.
            }
            FILTER( REGEX( STR(?l), "$search_param", "i") )
        }
    }
}

singleton.query:

PREFIX skos: <http://www.w3.org/2004/02/skos/core#> SELECT ?root WHERE {
                BIND (<$search_param> as ?root)
}

sparql.query:

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
    SELECT ?c ?l ?a
    WHERE {
    ?c rdf:type skos:Concept.
    <http://vocab.nerc.ac.uk/collection/P10/current/> skos:member ?c.
    OPTIONAL {
    ?c skos:prefLabel ?l.
    FILTER( LANG(?l) = "en")
    }
    OPTIONAL {
    ?c skos:prefLabel ?a.
    FILTER( LANG(?a) = "it")
    }
    FILTER( REGEX( STR(?l), "$search_param", "i") || REGEX( STR(?a), "$search_param", "i") )
    }
    ORDER BY ASC(?l) ASC(?a)

singleton.query:

PREFIX addr: <http://wymiwyg.org/ontologies/foaf/postaddress#>
PREFIX vcard: <http://www.w3.org/2006/vcard/ns#>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>
SELECT DISTINCT ?phone ?addr ?town ?region ?postcode ?country ?email ?homepage
  WHERE {
  OPTIONAL {
    <$search_param> foaf:phone ?phone.
  }
  OPTIONAL {
    <$search_param> vcard:email ?email.
  }
  OPTIONAL {
    <$search_param> foaf:homepage ?homepage.
  }
  OPTIONAL {
    <$search_param> addr:address ?b1.
  }
  OPTIONAL {
    ?b1 addr:deliveryPoint ?b2.
  }
  OPTIONAL {
    ?b2 addr:location ?b3.
  }
  OPTIONAL {
    ?b3 addr:thoroughfareName ?addr.
  }
  OPTIONAL {
    ?b3 addr:streetNr ?l2.
  }
  OPTIONAL {
    ?b3 addr:town ?town.
  }
  OPTIONAL {
    ?b3 addr:region ?region.
  }
  OPTIONAL {
    ?b3 addr:postcode ?postcode.
  }
  OPTIONAL {
    ?b3 addr:country ?country.
  }
}

sparql.query:

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>        
SELECT ?c ?l ?a ?urn                       
FROM <http://ritmare.it/rdfdata/parameters>
WHERE { 
  ?c rdf:type skos:Concept.
  OPTIONAL {
    ?c skos:notation ?urn. 
  }
  OPTIONAL {
    ?c skos:prefLabel ?l.
    FILTER( LANG(?l) = 'en') 
  }
  OPTIONAL {
    ?c skos:prefLabel ?a.
    FILTER( LANG(?a) = 'it') 
  }
  FILTER( REGEX( STR(?l), '$search_param', 'i') || REGEX( STR(?a), '$search_param', 'i') ) 
}
ORDER BY ASC(?l) ASC(?a)


PREFIX skos: <http://www.w3.org/2004/02/skos/core#> 
SELECT ?c ?l ?a ?urn
WHERE { 
  ?c rdf:type skos:Concept.
  ?c skos:inScheme <http://vocab.nerc.ac.uk/collection/L05/current/>.
  OPTIONAL {
    ?c skos:notation ?urn.
  }
  OPTIONAL {
    ?c skos:prefLabel ?l.
    FILTER( LANG(?l) = "en")
  }
  OPTIONAL {
    ?c skos:prefLabel ?a.
    FILTER( LANG(?a) = "it")
  }
  FILTER( REGEX( STR(?l), "$search_param", "i") || REGEX( STR(?a), "$search_param", "i") )
}
ORDER BY ASC(?l) ASC(?a)

singleton.query:
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
SELECT ?c ?l ?urn
FROM <http://ritmare.it/rdfdata/units>
WHERE {
  ?c rdf:type skos:Concept.
  OPTIONAL {
    ?c skos:prefLabel ?l.
    FILTER( LANG(?l) = "en")
  }
  OPTIONAL {
    ?c skos:prefLabel ?a.
    FILTER( LANG(?a) = "en")
  }
  OPTIONAL {
    ?c skos:altLabel ?urn.
    FILTER( LANG(?urn) = "en")
  }
  FILTER( REGEX( STR(?l), "$search_param", "i") || REGEX( STR(?a), "$search_param", "i") )
}
ORDER BY ASC(?l) ASC(?a)

                PREFIX vcard: <http://www.w3.org/2006/vcard/ns#>
                PREFIX foaf: <http://xmlns.com/foaf/0.1/>
                PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
                        SELECT ?fullname ?email ?phone ?homepage ?l ?addr ?town ?postcode ?country
                        WHERE { 
                          OPTIONAL {
                            <$search_param> foaf:fullName ?fullname .
                          }
                          OPTIONAL {
                            <$search_param> foaf:mbox ?email .
                          }
  						  OPTIONAL {
                            <$search_param> foaf:phone ?phone .
                          }
                          OPTIONAL {
                            <$search_param> vcard:org ?b3 .
                            ?b3 foaf:homepage ?homepage .
                            ?b3 foaf:name ?l .
                          }
                          {
                            <$search_param> vcard:org/vcard:hasAddress ?b1 .
                            ?b1 vcard:street-address ?addr .
                            ?b1 vcard:locality ?town .
                            ?b1 vcard:postal-code ?postcode .
                            ?b1 vcard:country-name ?b2 .
                            #?b2 foaf:name ?country .
                          }
  							#UNION {
                          #  ?b3 vcard:hasAddress ?b1 .
                          #  ?b1 vcard:street-address ?addr .
                          #  ?b1 vcard:locality ?town .
                          #  ?b1 vcard:postal-code ?postcode .
                          #  ?b1 vcard:country-name ?b2 .
                          #  ?b2 foaf:name ?country .
                          #}
                          #FILTER(lang(?l)='it')
						  SERVICE <http://sparql.get-it.it> {
    						?b2 foaf:name ?country . 
  						  }
                        }
                        LIMIT 1


