    }
    ORDER BY ASC(?l)


PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
SELECT *
WHERE {
{
?c skos:inScheme <http://inspire.ec.europa.eu/codelist/LegislationLevelValue> .
    ?c skos:prefLabel ?l.
    }
    FILTER( REGEX( STR(?l), "$search_param", "i") )
    }
    ORDER BY ASC(?l)


PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
