datasource.query:

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
SELECT ?c ?l ?a
FROM <http://ritmare.it/rdfdata/reference>
WHERE {
  ?c rdf:type skos:Concept .
  OPTIONAL {
    ?c skos:prefLabel ?l .
    FILTER( LANG(?l) = "en")
  }
  OPTIONAL {
    ?c skos:prefLabel ?a .
    FILTER( LANG(?a) = "it")
  }
  FILTER( REGEX( STR(?l), "$search_param", "i") || REGEX( STR(?a), "$search_param", "i") )
}
ORDER BY ASC(?l) ASC(?a)


PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
SELECT ?c ?a ?l ?date
WHERE {
  {
    <$search_param> foaf:topic_interest ?t .
  } UNION {
    ?father foaf:member <$search_param> .
    ?father foaf:topic_interest ?t .
  }
  ?t skos:prefLabel ?a .
  ?t skos:inScheme ?c .
  ?c skos:prefLabel ?l .
  ?c dct:issued ?date .
  FILTER( LANG(?l) = "en" && LANG(?a) = "en")
} LIMIT 1
