datasource.query:

PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
SELECT ?c ?l ?a
FROM <http://ritmare.it/rdfdata/reference>
WHERE {
  ?c rdf:type skos:Concept .
  OPTIONAL {
    ?c skos:prefLabel ?l .
    FILTER( LANG(?l) = "en")
  }
  OPTIONAL {
    ?c skos:prefLabel ?a .
    FILTER( LANG(?a) = "it")
  }
  FILTER( REGEX( STR(?l), "$search_param", "i") || REGEX( STR(?a), "$search_param", "i") )
}
ORDER BY ASC(?l) ASC(?a)
